mapper.select:
select id, project_name, createtime, updatetime, user_id, (SELECT COUNT(0) from t_notice WHERE t_notice.project_id = t_project.id) as notice_count, (SELECT COUNT(0) from t_text WHERE t_text.project_id = t_project.id) as text_count, (SELECT COUNT(0) from t_version WHERE t_version.project_id = t_project.id) as version_count, (SELECT COUNT(0) from t_feedback WHERE t_feedback.project_id = t_project.id) as feedback_count from t_project where user_id = #{userId}
and project_name like #{pattern}
order by createtime desc
